   620	     * Handles the submission of the edit feed form.
   621	     * @param {Event} event - The form submission event.
   622	     */
   623	    async function handleEditFeedSubmit(event) {
   624	        event.preventDefault();
   625	        
   626	        const modal = document.getElementById('edit-feed-modal');
   627	        const feedIdInput = document.getElementById('edit-feed-id');
   628	        const feedUrlInput = document.getElementById('edit-feed-url');
   629	        const saveButton = document.getElementById('save-feed-button');
   630	        
   631	        const feedId = parseInt(feedIdInput.value);
   632	        const newUrl = feedUrlInput.value.trim();
   633	        const errorElement = document.getElementById('edit-feed-error');
   634	        
   635	        // Clear previous errors
   636	        errorElement.style.display = 'none';
   637	        
   638	        if (!newUrl) {
   639	            errorElement.textContent = 'Please enter a feed URL.';
   640	            errorElement.style.display = 'block';
   641	            return;
   642	        }
   643	        
   644	        // Disable the save button and show loading state
   645	        const originalButtonText = saveButton.textContent;
   646	        saveButton.disabled = true;
   647	        saveButton.textContent = 'Saving...';
   648	        
   649	        try {
   650	            const result = await fetchData(`/api/feeds/${feedId}`, {
   651	                method: 'PUT',
   652	                headers: {
   653	                    'Content-Type': 'application/json'
   654	                },
   655	                body: JSON.stringify({ url: newUrl })
   656	            });
   657	            
   658	            console.log('Feed updated successfully:', result);
   659	            // Close the modal
   660	            modal.classList.remove('is-active');
   661	            
   662	            // Update just the edited widget instead of reloading entire tab
   663	            const widget = document.querySelector(`.feed-widget[data-feed-id="${feedId}"]`);
   664	            if (widget) {
   665	                // Replace the widget with updated content
   666	                const newWidget = createFeedWidget(result);
   667	            widget.replaceWith(newWidget);
   668	        } else {
   669	            // Fallback: reload the tab if widget not found
   670	            console.warn('Widget not found, falling back to tab reload');
   671	            if (loadedTabs.has(activeTabId)) {
   672	                document.querySelectorAll(`.feed-widget[data-tab-id="${activeTabId}"]`).forEach(w => w.remove());
   673	            loadedTabs.delete(activeTabId);
   674	        }
   675	        await setActiveTab(activeTabId);
   676	        await initializeTabs(true); // Update unread counts
   677	        } catch (error) {
   678	            console.error('Error updating feed:', error);
   679	            const errorMsg = error.message || 'An unexpected error occurred.';
   680	            // Extract the backend message if available for a cleaner display.
   681	            const backendMsgIndex = errorMsg.indexOf('message: ');
   682	            errorElement.textContent = backendMsgIndex !== -1 
   683	                ? errorMsg.substring(backendMsgIndex + 9)
   684	                : errorMsg;
   685	            errorElement.style.display = 'block';
   686	        } finally {
   687	            // Re-enable the save button
   688	            saveButton.disabled = false;
   689	            saveButton.textContent = originalButtonText;
   690	        }
   691	    }
