   620	    /**
   621	     * Handles the submission of the edit feed form.
   622	     * @param {Event} event - The form submission event.
   623	     */
   624	    async function handleEditFeedSubmit(event) {
   625	        event.preventDefault();
   626	        
   627	        const modal = document.getElementById('edit-feed-modal');
   628	        const feedIdInput = document.getElementById('edit-feed-id');
   629	        const feedUrlInput = document.getElementById('edit-feed-url');
   630	        const saveButton = document.getElementById('save-feed-button');
   631	        
   632	        const feedId = parseInt(feedIdInput.value);
   633	        const newUrl = feedUrlInput.value.trim();
   634	        const errorElement = document.getElementById('edit-feed-error');
   635	        
   636	        // Clear previous errors
   637	        errorElement.style.display = 'none';
   638	        
   639	        if (!newUrl) {
   640	            errorElement.textContent = 'Please enter a feed URL.';
   641	            errorElement.style.display = 'block';
   642	            return;
   643	        }
   644	        
   645	        // Disable the save button and show loading state
   646	        const originalButtonText = saveButton.textContent;
   647	        saveButton.disabled = true;
   648	        saveButton.textContent = 'Saving...';
   649	        
   650	        try {
   651	            const result = await fetchData(`/api/feeds/${feedId}`, {
   652	                method: 'PUT',
   653	                headers: {
   654	                    'Content-Type': 'application/json'
   655	                },
   656	                body: JSON.stringify({ url: newUrl })
   657	            });
   658	            
   659	            if (result) {
   660	                console.log('Feed updated successfully:', result);
   661	                // Close the modal
   662	                modal.classList.remove('is-active');
   663	                
   664	                // Update just the edited widget instead of reloading entire tab
   665	                const widget = document.querySelector(`.feed-widget[data-feed-id="${feedId}"]`);
   666	                if (widget) {
   667	                    // Replace the widget with updated content
   668	                    const newWidget = createFeedWidget(result);
   669	                    widget.replaceWith(newWidget);
   670	                } else {
   671	                    // Fallback: reload the tab if widget not found
   672	                    console.warn('Widget not found, falling back to tab reload');
   673	                    if (loadedTabs.has(activeTabId)) {
   674	                        document.querySelectorAll(`.feed-widget[data-tab-id="${activeTabId}"]`).forEach(w => w.remove());
   675	                        loadedTabs.delete(activeTabId);
   676	                    }
   677	                    await setActiveTab(activeTabId);
   678	                }
   679	                await initializeTabs(true); // Update unread counts
   680	            } else {
   681	                console.error('Failed to update feed.');
   682	            }
   683	        } catch (error) {
   684	            console.error('Error updating feed:', error);
   685	            const errorMsg = error.message || 'An unexpected error occurred.';
   686	            // Extract the backend message if available for a cleaner display.
   687	            const backendMsgIndex = errorMsg.indexOf('message: ');
   688	            errorElement.textContent = backendMsgIndex !== -1 
   689	                ? errorMsg.substring(backendMsgIndex + 9)
   690	                : errorMsg;
   691	            errorElement.style.display = 'block';
   692	        } finally {
   693	            // Re-enable the save button
   694	            saveButton.disabled = false;
   695	            saveButton.textContent = originalButtonText;
   696	        }
   697	    }
