   440	    async function loadFeedsForTab(tabId) {
   441	        // Show a loading message only if the grid is completely empty
   442	        if (feedGrid.children.length === 0) {
   443	            feedGrid.innerHTML = '<p>Loading feeds...</p>';
   444	        }
   445	
   446	        const feedsWithItems = await fetchData(`/api/tabs/${tabId}/feeds`);
   447	
   448	        // If we were showing a global loading message, clear it.
   449	        if (feedGrid.querySelector('p')) {
   450	            feedGrid.innerHTML = '';
   451	        }
   452	
   453	        if (feedsWithItems === null) {
   454	            feedGrid.innerHTML = '<p>Error loading feeds. Please check the console or try again.</p>';
   455	            return;
   456	        }
   457	
   458	        if (feedsWithItems && feedsWithItems.length > 0) {
   459	            feedsWithItems.forEach(feed => {
   460	                const widget = createFeedWidget(feed);
   461	                feedGrid.appendChild(widget);
   462	            });
   463	        } else if (feedGrid.children.length === 0) {
   464	            // Only show 'no feeds' if the entire grid is empty after attempting to load
   465	            feedGrid.innerHTML = '<p>No feeds found for this tab. Add one using the form above!</p>';
   466	        }
   467	
   468	        loadedTabs.add(tabId); // Mark this tab's content as loaded
   469	    }
