620      /**
   621       * Handles the submission of the edit feed form.
   622       * @param {Event} event - The form submission event.
   623       */
   624      async function handleEditFeedSubmit(event) {
   625          event.preventDefault();
   626
   627          const modal = document.getElementById('edit-feed-modal');
   628          const feedIdInput = document.getElementById('edit-feed-id');
   629          const feedUrlInput = document.getElementById('edit-feed-url');
   630          const saveButton = document.getElementById('save-feed-button');
   631
   632          const feedId = parseInt(feedIdInput.value);
   633          const newUrl = feedUrlInput.value.trim();
   634          const errorElement = document.getElementById('edit-feed-error');
   635
   636          // Clear previous errors
   637          errorElement.style.display = 'none';
   638
   639          if (!newUrl) {
   640              errorElement.textContent = 'Please enter a feed URL.';
   641              errorElement.style.display = 'block';
   642              return;
   643          }
   644
   645          // Disable the save button and show loading state
   646          const originalButtonText = saveButton.textContent;
   647          saveButton.disabled = true;
   648          saveButton.textContent = 'Saving...';
   649
   650          try {
   651              const result = await fetchData(`/api/feeds/${feedId}`, {
   652                  method: 'PUT',
   653                  headers: {
   654                      'Content-Type': 'application/json'
   655                  },
   656                  body: JSON.stringify({ url: newUrl })
   657              });
   658
   659              console.log('Feed updated successfully:', result);
   660              // Close the modal
   661              modal.classList.remove('is-active');
   662
   663              // Update just the edited widget instead of reloading entire tab
   664              const widget = document.querySelector(`.feed-widget[data-feed-id="${feedId}"]`);
   665              if (widget) {
   666                  // Replace the widget with updated content
   667                  const newWidget = createFeedWidget(result);
   668              widget.replaceWith(newWidget);
   669          } else {
   670              // Fallback: reload the tab if widget not found
   671              console.warn('Widget not found, falling back to tab reload');
   672              if (loadedTabs.has(activeTabId)) {
   673                  document.querySelectorAll(`.feed-widget[data-tab-id="${activeTabId}"]`).forEach(w => w.remove());
   674              loadedTabs.delete(activeTabId);
   675          }
   676          await setActiveTab(activeTabId);
   677          await initializeTabs(true); // Update unread counts
   678          } catch (error) {
   679              console.error('Error updating feed:', error);
   680              errorElement.textContent = error.message || 'An unexpected error occurred.';
   681          } finally {
   682              // Re-enable the save button
   683              saveButton.disabled = false;
   684              saveButton.textContent = originalButtonText;
   685          }
   686      }
