        grid-template-columns: 1fr;
    }
}

extras.new_content:
@media (max-width: 480px) {
    header h1 {
        font-size: 1.3em;
    }

    #tabs-container button {
        padding: 8px 10px;
        font-size: 0.9em;
    }

    #feed-grid {
        /* Single column on very small screens */
        grid-template-columns: 1fr;
    }
}

/* Feed Widget Buttons */
.feed-widget-buttons {
    position: absolute;
    top: 8px;
    right: 8px;
    display: flex;
    gap: 4px;
    z-index: 10;
}

.feed-widget .edit-feed-button,
.feed-widget .delete-feed-button {
    background: #6c757d;
    color: white;
    border: none;
    border-radius: 50%;
    width: 18px;
    height: 18px;
    font-size: 11px;
    line-height: 17px;
    text-align: center;
    cursor: pointer;
    opacity: 0.6;
    transition: opacity 0.2s ease-in-out, background-color 0.2s ease-in-out;
}

.feed-widget:hover .edit-feed-button,
.feed-widget:hover .delete-feed-button {
    opacity: 1;
}

.feed-widget .edit-feed-button:hover {
    background-color: #007bff; /* Blue on hover */
}

.feed-widget .delete-feed-button:hover {
    background-color: #dc3545; /* Red on hover */
}

/* Modal Styles */
.modal {
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background-color: rgba(0, 0, 0, 0.5);
    display: flex;
    justify-content: center;
    align-items: center;
    z-index: 1000;
}

.modal-content {
    background-color: white;
    padding: 20px;
    border-radius: 8px;
    box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
    width: 90%;
    max-width: 500px;
}

.modal h2 {
    margin-top: 0;
    margin-bottom: 20px;
    color: #333;
}

.form-group {
    margin-bottom: 15px;
}

.form-group label {
    display: block;
    margin-bottom: 5px;
    font-weight: 600;
}

.form-group input {
    width: 100%;
    padding: 8px;
    border: 1px solid #ddd;
    border-radius: 4px;
    box-sizing: border-box;
}

.form-group input[readonly] {
    background-color: #f5f5f5;
    color: #666;
}

.form-group small {
    display: block;
    margin-top: 5px;
    color: #666;
    font-size: 0.8em;
}

.modal-buttons {
    display: flex;
    gap: 10px;
    justify-content: flex-end;
    margin-top: 20px;
}

.modal-buttons button {
    padding: 8px 16px;
    border: none;
    border-radius: 4px;
    cursor: pointer;
    font-size: 0.9em;
}

#save-feed-button {
    background-color: #007bff;
    color: white;
}

#save-feed-button:hover {
    background-color: #0056b3;
}

#cancel-edit-button {
    background-color: #6c757d;
    color: white;
}

#cancel-edit-button:hover {
    background-color: #5a6268;
}

