   340	     * Renders a single feed widget and appends it to the main grid.
   341	     * @param {object} feed - The feed object from the API (including unread_count and items).
   342	     */
   343	    function renderFeedWidget(feed) {
   344	        const widget = document.createElement('div');
   345	        widget.classList.add('feed-widget');
   346	        widget.dataset.feedId = feed.id;
   347	        widget.dataset.tabId = feed.tab_id; // Associate widget with a tab
   348	
   349	        const deleteButton = document.createElement('button');
   350	        deleteButton.classList.add('delete-feed-button');
   351	        deleteButton.textContent = 'X';
   352	        deleteButton.title = 'Delete Feed';
   353	        deleteButton.addEventListener('click', (e) => {
   354	            e.stopPropagation();
   355	            handleDeleteFeed(feed.id);
   356	        });
   357	        widget.appendChild(deleteButton);
   358	
   359	        const titleElement = document.createElement('h2');
   360	        const titleTextNode = document.createTextNode(feed.name); // Create text node for the name
   361	
   362	        // Determine the link for the feed title
   363	        const feedLinkUrl = feed.site_link || feed.url; // Prioritize site_link, fallback to feed's own XML URL
   364	
   365	        if (feedLinkUrl) {
   366	            const titleLink = document.createElement('a');
   367	            titleLink.href = feedLinkUrl;
   368	            titleLink.target = '_blank'; // Open in new tab
   369	            titleLink.rel = 'noopener noreferrer'; // Security measure
   370	            titleLink.appendChild(titleTextNode); // Add name text to link
   371	            titleElement.appendChild(titleLink);
   372	        } else {
   373	            titleElement.appendChild(titleTextNode); // Add name text directly if no link
   374	        }
   375	
   376	        widget.appendChild(titleElement);
   377	        
   378	        const badge = createBadge(feed.unread_count);
   379	        if (badge) {
   380	            // Append badge after the link/text within H2, or adjust styling as needed
   381	            titleElement.appendChild(badge);
   382	        }
   383	        // titleElement.prepend(feed.name); // Removed, name is now part of link or direct text node
   384	
   385	        const itemList = document.createElement('ul');
   386	        widget.appendChild(itemList);
   387	
   388	        // Append the whole widget to the grid
   389	        feedGrid.appendChild(widget);
   390	
   391	        // Render items
   392	        if (feed.items && feed.items.length > 0) {
   393	            feed.items.forEach(item => {
   394	                const listItem = document.createElement('li');
   395	                listItem.dataset.itemId = item.id;
   396	                listItem.classList.add(item.is_read ? 'read' : 'unread');
   397	
   398	                const link = document.createElement('a');
   399	                link.href = item.link;
   400	                link.textContent = item.title;
   401	                link.target = '_blank';
   402	                link.addEventListener('click', () => handleMarkItemRead(item.id, listItem, feed.id, feed.tab_id));
   403	                listItem.appendChild(link);
   404	
   405	                const timestamp = document.createElement('span');
   406	                timestamp.textContent = formatDate(item.published_time || item.fetched_time);
   407	                listItem.appendChild(timestamp);
   408	
   409	                itemList.appendChild(listItem);
   410	            });
   411	        } else {
   412	            itemList.innerHTML = '<li>No items found for this feed.</li>';
   413	        }
   414	    }
