   416	    /**
   417	     * Loads and renders all feeds for a given tab ID.
   418	     * @param {number} tabId - The ID of the tab to load feeds for.
   419	     */
   420	    async function loadFeedsForTab(tabId) {
   421	        // Show a loading message only if the grid is completely empty
   422	        if (feedGrid.children.length === 0) {
   423	            feedGrid.innerHTML = '<p>Loading feeds...</p>';
   424	        }
   425	
   426	        const feedsWithItems = await fetchData(`/api/tabs/${tabId}/feeds`);
   427	
   428	        // If we were showing a global loading message, clear it.
   429	        if (feedGrid.querySelector('p')) {
   430	            feedGrid.innerHTML = '';
   431	        }
   432	
   433	        if (feedsWithItems === null) {
   434	            feedGrid.innerHTML = '<p>Error loading feeds. Please check the console or try again.</p>';
   435	            return;
   436	        }
   437	
   438	        if (feedsWithItems && feedsWithItems.length > 0) {
   439	            feedsWithItems.forEach(feed => {
   440	                renderFeedWidget(feed);
   441	            });
   442	        } else if (feedGrid.children.length === 0) {
   443	            // Only show 'no feeds' if the entire grid is empty after attempting to load
   444	            feedGrid.innerHTML = '<p>No feeds found for this tab. Add one using the form above!</p>';
   445	        }
   446	
   447	        loadedTabs.add(tabId); // Mark this tab's content as loaded
   448	    }
