   840	    async function initializeTabs(isUpdate = false) {
   841	        const currentActiveId = isUpdate ? activeTabId : localStorage.getItem('activeTabId');
   842	        const tabs = await fetchData('/api/tabs');
   843	        if (tabs) {
   844	            // Attempt to restore activeTabId from localStorage if not doing a specific update that preserves it
   845	            let storedActiveTabId = localStorage.getItem('activeTabId');
   846	            if (storedActiveTabId && tabs.some(t => t.id == storedActiveTabId)) {
   847	                activeTabId = parseInt(storedActiveTabId);
   848	            } else if (currentActiveId && tabs.some(t => t.id == currentActiveId)) {
   849	                activeTabId = parseInt(currentActiveId); // Use current if valid and stored is not
   850	            } else {
   851	                activeTabId = null; // Fallback if stored/current is invalid
   852	                localStorage.removeItem('activeTabId'); // Clean up invalid stored ID
   853	            }
   854	            renderTabs(tabs);
   855	        } else {
   856	            renderTabs([]);
   857	            localStorage.removeItem('activeTabId'); // No tabs, so no active tab
