   610	    /**
   611	     * Handles the submission of the edit feed form.
   612	     * @param {Event} event - The form submission event.
   613	     */
   614	    async function handleEditFeedSubmit(event) {
   615	        event.preventDefault();
   616	        
   617	        const modal = document.getElementById('edit-feed-modal');
   618	        const feedIdInput = document.getElementById('edit-feed-id');
   619	        const feedUrlInput = document.getElementById('edit-feed-url');
   620	        const saveButton = document.getElementById('save-feed-button');
   621	        
   622	        const feedId = parseInt(feedIdInput.value);
   623	        const newUrl = feedUrlInput.value.trim();
   624	        
   625	        if (!newUrl) {
   626	            alert('Please enter a feed URL');
   627	            return;
   628	        }
   629	        
   630	        // Disable the save button and show loading state
   631	        const originalButtonText = saveButton.textContent;
   632	        saveButton.disabled = true;
   633	        saveButton.textContent = 'Saving...';
   634	        
   635	        try {
   636	            const result = await fetchData(`/api/feeds/${feedId}`, {
   637	                method: 'PUT',
   638	                headers: {
   639	                    'Content-Type': 'application/json'
   640	                },
   641	                body: JSON.stringify({ url: newUrl })
   642	            });
   643	            
   644	            if (result) {
   645	                console.log('Feed updated successfully:', result);
   646	                // Close the modal
   647	                modal.style.display = 'none';
   648	                
   649	                // Reload the current tab to show the updated feed
   650	                if (loadedTabs.has(activeTabId)) {
   651	                    document.querySelectorAll(`.feed-widget[data-tab-id="${activeTabId}"]`).forEach(w => w.remove());
   652	                    loadedTabs.delete(activeTabId);
   653	                }
   654	                await setActiveTab(activeTabId);
   655	                await initializeTabs(true);
   656	            } else {
   657	                console.error('Failed to update feed.');
   658	            }
   659	        } catch (error) {
   660	            console.error('Error updating feed:', error);
   661	        } finally {
   662	            // Re-enable the save button
   663	            saveButton.disabled = false;
   664	            saveButton.textContent = originalButtonText;
   665	        }
   666	    }
