   681	     * @param {HTMLElement} listItemElement - The <li> element of the item.
   682	     * @param {number} feedId - The ID of the parent feed.
   683	     * @param {number} tabId - The ID of the parent tab.
   684	     */
   685	    async function handleMarkItemRead(itemId, listItemElement, feedId, tabId) {
   686	        if (listItemElement.classList.contains('unread')) {
   687	            console.log(`Marking item ${itemId} as read`);
   688	            const result = await fetchData(`/api/items/${itemId}/read`, { method: 'POST' });
   689	
   690	            if (result && result.success) {
   691	                console.log(`Successfully marked item ${itemId} as read.`);
   692	                listItemElement.classList.remove('unread');
   693	                listItemElement.classList.add('read');
   694	                updateUnreadCount(feedId, -1);
   695	                updateUnreadCount(tabId, -1, true);
   696	            } else {
   697	                console.error(`Failed to mark item ${itemId} as read.`);
   698	            }
   699	        }
   700	    }
