   362	        // Determine the link for the feed title
   363	        const feedLinkUrl = feed.site_link || feed.url; // Prioritize site_link, fallback to feed's own XML URL
   364	
   365	        if (feedLinkUrl) {
   366	            const titleLink = document.createElement('a');
   367	            titleLink.href = feedLinkUrl;
   368	            titleLink.target = '_blank'; // Open in new tab
