   442	        if (feedGrid.querySelector('p')) {
   443	            feedGrid.innerHTML = '';
   444	        }
   445	
   446	        if (feedsWithItems === null) {
   447	            feedGrid.innerHTML = '<p>Error loading feeds. Please check the console or try again.</p>';
   448	            return;
   449	        }
   450	
   451	        if (feedsWithItems && feedsWithItems.length > 0) {
   452	            feedsWithItems.forEach(feed => {
