   606	    /**
   607	     * Handles the submission of the edit feed form.
   608	     * @param {Event} event - The form submission event.
   609	     */
   610	    async function handleEditFeedSubmit(event) {
   611	        event.preventDefault();
   612	        
   613	        const modal = document.getElementById('edit-feed-modal');
   614	        const feedIdInput = document.getElementById('edit-feed-id');
   615	        const feedUrlInput = document.getElementById('edit-feed-url');
   616	        const saveButton = document.getElementById('save-feed-button');
   617	        
   618	        const feedId = parseInt(feedIdInput.value);
   619	        const newUrl = feedUrlInput.value.trim();
   620	        
   621	        if (!newUrl) {
   622	            alert('Please enter a feed URL');
   623	            return;
   624	        }
   625	        
   626	        // Disable the save button and show loading state
   627	        const originalButtonText = saveButton.textContent;
   628	        saveButton.disabled = true;
   629	        saveButton.textContent = 'Saving...';
   630	        
   631	        try {
   632	            const result = await fetchData(`/api/feeds/${feedId}`, {
   633	                method: 'PUT',
   634	                headers: {
   635	                    'Content-Type': 'application/json'
   636	                },
   637	                body: JSON.stringify({ url: newUrl })
   638	            });
   639	            
   640	            if (result) {
   641	                console.log('Feed updated successfully:', result);
   642	                // Close the modal
   643	                modal.style.display = 'none';
   644	                
   645	                // Update just the edited widget instead of reloading entire tab
   646	                const widget = document.querySelector(`.feed-widget[data-feed-id="${feedId}"]`);
   647	                if (widget) {
   648	                    // Replace the widget with updated content
   649	                    const newWidget = createFeedWidget(result);
   650	                    widget.replaceWith(newWidget);
   651	                } else {
   652	                    // Fallback: reload the tab if widget not found
   653	                    console.warn('Widget not found, falling back to tab reload');
   654	                    if (loadedTabs.has(activeTabId)) {
   655	                        document.querySelectorAll(`.feed-widget[data-tab-id="${activeTabId}"]`).forEach(w => w.remove());
   656	                        loadedTabs.delete(activeTabId);
   657	                    }
   658	                    await setActiveTab(activeTabId);
   659	                }
   660	                await initializeTabs(true); // Update unread counts
   661	            } else {
   662	                console.error('Failed to update feed.');
   663	            }
   664	        } catch (error) {
   665	            console.error('Error updating feed:', error);
   666	        } finally {
   667	            // Re-enable the save button
   668	            saveButton.disabled = false;
   669	            saveButton.textContent = originalButtonText;
   670	        }
   671	    }
